mapper.select:
select * from t_order


        and id = #{id}


        and member_id = #{memberId}


        and orderDate = #{orderDate}


        and orderType = #{orderType}


        and orderStatus = #{orderStatus}


        and setmeal_id = #{setmealId}

SELECT m.`name` memberName, m.`phoneNumber` , o.`orderDate`, o.`id`, o.`orderType`, o.`orderStatus`, s.`name` setmealName FROM t_member m , t_order o , t_setmeal s WHERE o.`setmeal_id` = s.`id` AND m.`id`=o.`member_id`
and (m.name like concat("%",#{queryString},"%") or m.phoneNumber like concat("%",#{queryString},"%"))
and (o.orderDate between #{startDate} and #{endDate})
and o.orderType = #{queryOrderType}
and o.orderStatus = #{queryOrderStatus}
